function init()
{
	curWinLoc = document.location.href;
	
	reloadPrefs();

	getAllHrefs();
	
	setInterval(function(){  getAllHrefs()  }, 500);
	
	document.addEventListener("click",function(event){
										click_document();
								}, false);
								

	// вычислим левый край								
	var b = document.createElement("div");
	b.setAttribute("style","z-index:-1; position:absolute ");
	b.style.left="0px";
	b.style.top="0px";
	b.style.width="200px";
	b.style.height="100px";
	document.body.appendChild( b );
	var PositionBody = b.getBoundingClientRect();
	Position_Left = PositionBody.left;
	document.body.removeChild( b );

	// сигнал о успешном load
	chrome.extension.sendRequest({akce:"content_load_success" }  );
	
}

// ----------------------------------
function reloadPrefs()
{
	var topW = window.screenTop;
	var leftW = window.screenLeft;
	var widthW = window.outerWidth;
	var widthS = window.screen.availWidth;

	chrome.extension.sendRequest({akce:"get_pref", left: leftW, width: widthW},
									function( response ){
										showTime=response.showTime;
										closeTime=response.closeTime;
										closePreviewTime=response.closePreviewTime;
										offset_x = response.offset_x;
										offset_y = response.offset_y;
										pos_mouse = response.pos_mouse;
										
										isServiceGoogle	= response.isServiceGoogle;
										isServiceMcAfee	= response.isServiceMcAfee;
										isServiceNorton	= response.isServiceNorton;
										isServiceWOT	= response.isServiceWOT;
										isServiceAvast	= response.isServiceAvast;
										isServiceTrust	= response.isServiceTrust;
										isServiceDrWeb	= response.isServiceDrWeb;
										
										isShowIcons	= response.isShowIcons;
										isEnableSafe	= response.isEnableSafe;
										isEnableLive	= response.isEnableLive;
										modeLive	= response.modeLive;
										
										createIframe();
										createIframeLive();
									}
								)
}


// ----------------------------------
function getAllHrefs()
{
	var elems = document.getElementsByTagName("a");
	for(var i=0; i<elems.length; i++)
	{
		var n = elems[i];
		try
		{
			n.removeEventListener("mouseover",hrefMouseOver,false);
			n.removeEventListener("mouseout",hrefMouseOut,false)
		}
		catch(r){}
		
		try
		{
			n.addEventListener("mouseover",hrefMouseOver,false);
			n.addEventListener("mouseout",hrefMouseOut,false)
		}
		catch(r){}
	}
}

// ----------------------------------
function hrefMouseOver()
{
	if (!isShowIcons) return;
	var url = this.getAttribute("href");
	if (!url) return;	
	activeUrl = url;
	previewOpen( this );
	
}

// ----------------------------------
function hrefMouseOut()
{
	previewClose( );
}


// -----------------------------------   открыть иконку к адресу
function previewOpen( elem )  {

	clearTimeout(hideSmallDivTimer);  	hideSmallDivTimer=null;      // очистка таймера закрытия

	showSmallDivTimer = setTimeout(function() {   showSmallDiv( elem ); 	 },  showTime );

}

// ------------------------------------ закрыть иконку у адреса
function previewClose(  )  {

	clearTimeout(showSmallDivTimer);  	showSmallDivTimer=null;      // очистка таймера открытия

	hideSmallDivTimer=setTimeout(function(){	
							removeSmallDiv();		
						},  closeTime);

	
}	

// ---  скрыть иконку
function removeSmallDiv( fl )  {    

	if ( isShowingPreview ) return;
	if ( isShowingLivePreview ) return;
	
	// -------------
	var element_sp =  document.getElementById("sp_smallDivTip");
	
	if(element_sp != null)
	{
		while( element_sp.firstChild )
		{
			element_sp.removeChild( element_sp.firstChild );
		}
		document.body.removeChild( element_sp );
	
		window.clearTimeout(showSmallDivTimer);  	showSmallDivTimer=null;      // очистка таймера открытия
	}
	
	isShowingSmallDiv = false;
	curElem = null;
}	

	
// ---  показать иконку
function showSmallDiv( elem )  {    

	if (curElem && curElem == elem) return;
	if ( isShowingPreview ) return;
	if ( isShowingSmallDiv )	removeSmallDiv( true );
	curElem = elem;
	
	var rects = elem.getClientRects();
	var k = rects.length;
	var y = elem.ownerDocument.defaultView.pageYOffset;
	var x = elem.ownerDocument.defaultView.pageXOffset;
	
	for(var i=0; i<k; i++)
	{
		var t = rects[i];
		if ( elem.pageY < y + rects[i].bottom)   break;
	}
	rect_Y = y + t.bottom - 16;
	rect_X = t.right;	
	
	curLink = elem.getAttribute("href");

	chrome.extension.sendRequest({akce:"isLink",  u: curLink, l: curWinLoc  },	function( response ) { 		} 		);
}	
	

