// --------------------------------------------------------------------
function showPreview( px, py, cntx)
{
	isShowingPreview=true;
	// scrolling
	var top = document.body.scrollTop;
	var left = document.body.scrollLeft;

	// ширина окна	
	var width = document.documentElement.clientWidth;
	var height = document.documentElement.clientHeight;

	var pxs = px - left;
	var pys = py - top;
			
	if ( pys < 0)			{		py = py - pys + 100;			var pys = py - top;			}
	if ( pys > height)		{		py = py - pys + height - 300;	var pys = py - top;			}
	if ( pxs < 0)			{		px = px - pxs + 100;			var pxs = px - left;		}
	if ( pxs > width)		{		px = px - pxs + width - 300;	var pxs = px - left;		}

	var napr = 0;
	if ( (pxs + 300) > width )				{			napr = 1;			if (pys < small_height) napr = 2;		}	
	if ( ( (pys + small_height) > height )	
		&& ( (pys - small_height) > 10 ) )
	{			
		napr = 1;			if (pxs < 300) napr = 3;				
	}	
	if ( pys < 10 )							{			napr = 0;													}	
	
	if (napr == 1)		{			var ppx = px - 240;			var ppy = py - small_height;		}   
	else if (napr == 2)	{			var ppx = px - 240;			var ppy = py + 20;		}	
	else if (napr == 3)	{			var ppx = px + 20;			var ppy = py - small_height;		}	
	else 				{			var ppx = px + 20;			var ppy = py;			}	
	
	if (cntx)   // контекстное меню
	{
		if (napr == 0)		{			ppx = ppx - 120;			ppy = ppy - 100;		}
		else if (napr == 1)	{			ppx = ppx + 120;			ppy = ppy + 100;		}
		else if (napr == 2)	{			ppx = ppx - 120;			ppy = ppy + 100;		}
		else if (napr == 3)	{			ppx = ppx + 120;			ppy = ppy - 100;		}
	}
	

	var div = document.getElementById('sp_fullDivTip');
	if (div)
	{
		div.style.left=ppx+"px";
		div.style.top=ppy+"px";
		div.style.display="block";
		
		var div3 = document.getElementById('sp_fullDivTip_background');
		//div3.setAttribute("title",this.curLink);
		
		show_Div_Google( 20 );
		show_Div_WOT( 20, 24, '' );
		show_Div_McAfee( 20 );
		show_Div_Norton( 20 );
		show_Div_Avast( 20 );
		show_Div_Trust( 20 );
		show_Div_DrWeb( 20 );

		show_Div_Info(  );

		zapros_google( false );					
		zapros_mcafee( false );					
		zapros_norton( false );					
		zapros_wot( false );					
		zapros_avast( false );					
		zapros_trust( false );					
		zapros_drweb( false );					
	}
}

