{
	removeSmallDiv( true );	
	
	curLink = url;
	curHost = host;

	var scroll_left = document.body.scrollLeft;
	var scroll_top = document.body.scrollTop;
//	var px = mouse_x + scroll_left;
//	var py = mouse_y + scroll_top;
	var px = mouseX + scroll_left;
	var py = mouseY + scroll_top;

	showPreview( px, py, true);
}
