function initPreviewShowLiveFrame( url )  {   

	window.clearTimeout(hideSmallDivTimer);  	hideSmallDivTimer=null;      // очистка таймера закрытия

	setTimeout(function() {   
						chrome.extension.sendRequest({akce:"ShowLiveFrame",  u: url   } 	);
				},  10 );

}
function initPreviewHideLiveFrame(  )  {   

	hideLivePreview( );
	
}
function initPreviewShowLiveIncognito( url )  {   

	window.clearTimeout(hideSmallDivTimer);  	hideSmallDivTimer=null;      // очистка таймера закрытия

	setTimeout(function() {   
						chrome.extension.sendRequest({akce:"ShowLiveIncognito",  u: url   } 	);
				},  10 );

}
// ----------------------------------------
function hideLivePreview( )
{
	var container = document.getElementById("sp_fullDivLive");
	container.style.display="none";
	Live_iframe.style.display="none";
	Live_div.style.display="none";
	isShowingLivePreview = false;
	hideSmallDivTimer=setTimeout(function(){	removeSmallDiv();		},  10);
	
}
function createIframeLive()
{
	var div = document.getElementById('sp_fullDivLive');
	if( div != null)	document.body.removeChild( div );

	var t=iframeAttr[modeLive];
	var div = document.createElement("div");
	div.setAttribute("id","sp_fullDivLive");
	div.setAttribute("style","z-index:2147483674; border:1px solid #c9c9c9; position:fixed; right:2px; top:2px;  width:"+t.width_d+"px; height:"+t.height_d+"px; display:block; background-color: #fafafa;  overflow:visible; ");
	div.style.display="none";
	document.body.appendChild( div );
	
	div.addEventListener("mouseout",function(event){
									hideLivePreviewTimer = window.setTimeout(function(){	hideLivePreview();  }, closePreviewTime);
								},true);
		
	div.addEventListener("mouseover",function(event){
									window.clearTimeout( hideLivePreviewTimer );   hideLivePreviewTimer = null;
								},true);
	div.addEventListener("click",function(event){  	
										click_fullDivTip = true;
									}, false);

	// кнопки
	var divb = showLivePreview_Pag( div );   
					
	// показываем страницу
	Live_iframe=document.createElement("iframe");
	divb.appendChild(Live_iframe);
	Live_iframe.setAttribute("frameborder","0");
	Live_iframe.setAttribute("allowTransparency","true");
	Live_iframe.setAttribute("width",t.width);
	Live_iframe.setAttribute("height",t.height);
	Live_iframe.setAttribute("style","position:absolute !important; left:"+t.left+"px !important; top:"+t.top+"px !important; border:solid 1px #444444 !important; -webkit-transform:scale("+t.scale+") !important; ")
//	Live_iframe.setAttribute("sandbox","allow-same-origin allow-scripts allow-popups allow-forms");   // из-за этого не открывается на страницу
	Live_iframe.setAttribute("sandbox","allow-same-origin");   // из-за этого не открывается на страницу
	Live_iframe.style.display="none";
	
									
	Live_iframe.addEventListener("load",function( e ) {

						window.clearTimeout( error_Live_iframe_Timer );   error_Live_iframe_Timer = null;
						Live_load.style.display="none";
						Live_div.style.display="none";
						Live_iframe.style.display="block";
						
					},false);

	// показываем ссылку
	Live_div=document.createElement("div");
	divb.appendChild(Live_div);
	Live_div.setAttribute("style","position:absolute; left:10px;  top:130px; width:95%; height: 50px; text-align:center;  ")
	
	div_e1=document.createElement("div");
	Live_div.appendChild(div_e1);
	div_e1.setAttribute("style","font-family: Arial,Helvetica,SunSans-Regular,Sans-Serif;  font-size: 15px; color: black; cursor:pointer; ")
	div_e1.textContent = "Regular - error, show in window";
	div_e1.addEventListener("click",function(event){  	
	
										setTimeout(function() {   
																	chrome.extension.sendRequest({akce:"ShowLiveWindow",  u: curHref, m: modeLive   } 	);
															},  10 );
															
										hideLivePreview( );															
	
									}, false);
	Live_div.style.display="none";

	// показываем ссылку
	Live_load=document.createElement("div");
	divb.appendChild(Live_load);
	Live_load.setAttribute("style","position:absolute; left:30px;  top:150px; width:90%; height: 100px; text-align:center; ")
	var img_l = document.createElement("img");
	img_l.setAttribute("src", FILE_IMAGES[19]);
	Live_load.appendChild( img_l );
	Live_load.style.display="none";

					
}
// -----------------------------------------------------------------
function showLivePreview( url )
{
	if (isShowingLivePreview) hideLivePreview( );

	curHref = url;
	
	var div = document.getElementById('sp_fullDivLive');
	if( div != null)
	{
		Live_load.style.display="block";
		div.style.display="block";
		try
		{
			Live_iframe.setAttribute("src",url);
			
			error_Live_iframe_Timer=setTimeout(function(){	
								
								Live_load.style.display="none";
								Live_iframe.style.display="none";
								Live_div.style.display="block";
								
								
							},  2000);
		}
		catch (e)	{		}	
//		Live_iframe.setAttribute("href",url);
		
		isShowingLivePreview = true;
	}	
}
// -----------------------------------------------------------------------------------------------------------------
function showLivePreview_Pag( div )  {      
	var t=iframeAttr[modeLive];
	var divb = document.createElement("div");
	divb.setAttribute("style","margin-top:10px; height:25px; position:relative;	width:"+t.width_d+"px;  display:block;  font-family: Arial,Helvetica,SunSans-Regular,Sans-Serif; font-size: 11px;  ");
 		
	var div1 = document.createElement("div");
	div1.setAttribute("class","SafePreview_button sp_fullDivPreview_current");
	div1.textContent="Current Page";
	divb.appendChild( div1 );
	div1.addEventListener("click", function( event ){
										Run_initPreviewHide( 10 );
										navigate_url( curHref, 0 );
									}, true);
		
	var div2 = document.createElement("div");
	div2.setAttribute("class","SafePreview_button sp_fullDivPreview_new");
	div2.textContent="New Page";
	divb.appendChild( div2 );
	div2.addEventListener("click", function( event ){
										Run_initPreviewHide( 1 );
										navigate_url( curHref, 1 );
									}, true);
		
	var div3 = document.createElement("div");
	div3.setAttribute("class","SafePreview_button sp_fullDivPreview_background");
//	div3.setAttribute("title", curHref);
	div3.textContent="Background";
	divb.appendChild( div3 );
	div3.addEventListener("click", function( event ){
										navigate_url( curHref, 2 );
									}, true);
		
									
	var img_close = document.createElement("img");
	img_close.setAttribute("title","Close");
	img_close.setAttribute("src",FILE_IMAGES[21]);
	img_close.setAttribute("class","sp_fullDivPreview_close");
	divb.appendChild( img_close );
	img_close.addEventListener("click", function( event ){
										hideLivePreview( );
										setTimeout(function() {   
													chrome.extension.sendRequest({akce:"Live_abort"  },	
																					function( response ){ 		} 
																				);
																},  10 );
									}, true);
									
	var img_set = document.createElement("img");
	img_set.setAttribute("title","Setting");
	img_set.setAttribute("src",FILE_IMAGES[23]);
	img_set.setAttribute("class","sp_fullDivPreview_setting");
	divb.appendChild( img_set );
	img_set.addEventListener("click", function( event ){	
										hideLivePreview( );
										display_settings( event );			
									}, true);
		
	div.appendChild( divb );
	
	var slct = document.createElement("select");
	slct.setAttribute("id","sp_fullDivPreview_modeLive");
	divb.appendChild( slct );
	var opt1 = document.createElement("option");
	opt1.setAttribute("value","small");
	opt1.textContent = "small"
	slct.appendChild( opt1 );
	var opt2 = document.createElement("option");
	opt2.setAttribute("value","middle");
	opt2.textContent = "middle"
	slct.appendChild( opt2 );
	var opt3 = document.createElement("option");
	opt3.setAttribute("value","big");
	opt3.textContent = "big"
	slct.appendChild( opt3 );
	slct.value = modeLive;
	slct.addEventListener("change", function( event ){	
										modeLive = this.value;
										createIframeLive();
										showLivePreview( curHref );
									}, true);
	
	return divb;
}

